import React from 'react';
import './intro.css';
import {Link} from 'react-scroll';
import btnImg from '../../assets/hireme.png'

const Intro = () => {
  return (
    <section id='intro'>
      <div class="introContent">
        <span class="introText">Hello, I'm <span class="introName">Shruti</span> <br/> Web Developer</span>
        <p class="introPara">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Blanditiis aliquid <br/> ullam minima voluptate molestias beatae!</p>
        <Link><button className='btn'> <img src={btnImg} alt="hire" className='btnImg' /> Hire Me</button></Link>
        <Link><button className='btn'> <img src={btnImg} alt="resume" className='btnImg' /> Hire Me</button></Link>
      </div>
      {/* <img src={Image.png} alt="Profile" className='bg' /> */}
    </section>
  )
}

export default Intro
