import React from "react";
import "./intro.css";
import { Link } from "react-scroll";
import btnImg from "../../assets/hireme.png";
import arrow from "../../assets/down-arrow.png";

const Intro = () => {
  return (
    <section id="intro">
      <div class="introContent">
        <span class="introText">
          Hello, I'm <span class="introName">Shruti</span> <br /> Web Developer
        </span>
        <p class="introPara">
          Lorem ipsum dolor sit amet, consectetur adipisicing elit. Blanditiis
          aliquid <br /> ullam minima voluptate molestias beatae!
        </p>
        <div className="introBtns">
          {/* Hire Me â€“ scroll or route */}
          <Link to="contact" smooth={true} duration={500}>
            <button className="btn">
              <img src={btnImg} alt="hire" className="btnImg" />
              Hire Me
            </button>
          </Link>

          {/* Download CV */}
          <a href="/resume.pdf" download>
            <button className="btn">
              <img src={arrow} alt="CV" className="btnImg" />
              Download CV
            </button>
          </a>
        </div>
      </div>
      {/* <img src={Image.png} alt="Profile" className='bg' /> */}
    </section>
  );
};

export default Intro;
