import React from "react";
import emailjs from "emailjs-com";
import "./contact.css";

const Contact = () => {
  const handleSubmit = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "YOUR_NEW_SERVICE_ID", // <-- replace with your new Service ID
        "YOUR_TEMPLATE_ID",    // <-- replace with your Template ID
        e.target,
        "YOUR_PUBLIC_KEY"      // <-- replace with your EmailJS Public Key
      )
      .then(
        (result) => {
          console.log("Email sent:", result.text);
          alert("Message sent successfully!");
        },
        (error) => {
          console.log("Error:", error.text);
          alert("Something went wrong. Please try again.");
        }
      );

    e.target.reset();
  };

  return (
    <section id="contact" className="contact-section">
      <h1 className="contact-title">
        Get in <span>touch</span>
      </h1>

      <div className="contact-container">
        {/* LEFT SIDE */}
        <div className="contact-left">
          <h2>Let‚Äôs talk</h2>
          <p>
            I‚Äôm currently available to take on new projects, so feel free to
            send me a message about anything you want me to work on.
          </p>

          <div className="contact-info">
            <p>üìß shrutigour415@gmail.com</p>
            <p>üìû +91 9826344340</p>
            <p>üìç Indore, India</p>
          </div>
        </div>

        {/* RIGHT SIDE */}
        <form className="contact-form" onSubmit={handleSubmit}>
          <label>Your Name</label>
          <input type="text" name="user_name" placeholder="Enter your name" required />

          <label>Your Email</label>
          <input type="email" name="user_email" placeholder="Enter your email" required />

          <label>Your Message</label>
          <textarea name="message" rows="6" placeholder="Enter your message" required></textarea>

          <button type="submit">Submit now</button>
        </form>
      </div>
    </section>
  );
};

export default Contact;
