import React from "react";
import "./intro.css";
import { Link } from "react-scroll";
import btnImg from "../../assets/hireme.png";
import arrow from "../../assets/down-arrow.png";
import resume from "../../assets/Shruti Resume.pdf";
import { FaInstagram } from "react-icons/fa";
import { FaLinkedin } from "react-icons/fa";
const Intro = () => {
  return (
    <section id="intro">
      <div class="introContent">
        <span class="introText">
          Hello, I'm <span class="introName">Shruti</span> <br /> Web Developer
        </span>
        <p class="introPara">
          Lorem ipsum dolor sit amet, consectetur adipisicing elit. Blanditiis
          aliquid <br /> ullam minima voluptate molestias beatae!
        </p>
        <div className="introBtns">
          {/* <Link to="contact" smooth={true} duration={500} className="btn1">
            <img src={btnImg} alt="hire" className="btnImg" />
            Hire Me
          </Link> */}

          <a href={resume} download className="btn2">
            <img src={arrow} alt="CV" className="btnImg arrowImg" />
            Download CV
          </a>

          <FaLinkedin size={40} color="#ffffff" className="linkedin" />
          <FaInstagram size={40} color="#ffffff" className="insta" />
        </div>
      </div>
      {/* <img src={Image.png} alt="Profile" className='bg' /> */}
    </section>
  );
};

export default Intro;
