import React, { useState } from "react";
import { Link } from "react-scroll";
import { FaBars, FaTimes } from "react-icons/fa";
import "./navbar.css";


const Navbar = () => {
  const offset = -80;
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <nav className="navbar">
      <div className="desktopMenu">
        <Link
          to="intro"
          className="desktopMenuListItem"
          spy={true}
          smooth={true}
          offset={offset}
          duration={500}
        >
          Home
        </Link>

        <Link
          to="about"
          className="desktopMenuListItem"
          spy={true}
          smooth={true}
          offset={offset}
          duration={500}
        >
          About
        </Link>

        <Link
          to="skills"
          className="desktopMenuListItem"
          spy={true}
          smooth={true}
          offset={offset}
          duration={500}
        >
          Skills
        </Link>

        <Link
          to="projects"
          className="desktopMenuListItem"
          spy={true}
          smooth={true}
          offset={offset}
          duration={500}
        >
          Projects
        </Link>

        <Link
          to="contact"
          className="desktopMenuListItem"
          spy={true}
          smooth={true}
          offset={offset}
          duration={500}
        >
          Contact
        </Link>
      </div>

      <a href="#contact">
        <button className="desktopMenuBtn">
          <img
            className="desktopMenuImg"
            width="48"
            height="48"
            src="https://img.icons8.com/fluency-systems-filled/48/user.png"
            alt="user"
          />
          Contact Me
        </button>
      </a>

      {/* Hamburger Icon */}
      <div className="hamburgerIcon" onClick={() => setMenuOpen(!menuOpen)}>
        {menuOpen ? (
          <FaTimes size={30} color="#ffffff" />
        ) : (
          <FaBars size={30} color="#ffffff" />
        )}
      </div>

    </nav>
  );
};

export default Navbar;
