import React, { useState } from "react";
import "./project.css";
import portfoli from '../../assets/portfolio.jpg'

const projectsData = [
  {
    title: "Portfolio Website",
    live: "https://your-live-site.netlify.app",
    github: "https://github.com/your-username/portfolio-react",
  },
  {
    title: "E-commerce Website",
    live: "https://ecommerce-live-link.netlify.app",
    github: "https://github.com/your-username/ecommerce-project",
  },
  {
    title: "Todo App",
    live: "https://todo-react.netlify.app",
    github: "https://github.com/your-username/todo-app",
  },
  {
    title: "Weather App",
    live: "https://weather-react.netlify.app",
    github: "https://github.com/your-username/weather-app",
  },
  {
    title: "Landing Page",
    live: "https://landingpage.netlify.app",
    github: "https://github.com/your-username/landing-page",
  },
  {
    title: "Blog Website",
    live: "https://blog-react.netlify.app",
    github: "https://github.com/your-username/blog-project",
  },
];


const Project = () => {
  const [visibleCount, setVisibleCount] = useState(6);

  const handleViewMore = () => {
    setVisibleCount((prev) => prev + 3);
  };

  return (
    <section id="projects" className="project-section">
      <h2 className="project-title">Projects</h2>




      {visibleCount < projectsData.length && (
        <div className="view-more-wrapper">
          <button className="view-more-btn" onClick={handleViewMore}>
            View More
          </button>
        </div>
      )}
    </section>
  );
};

export default Project;
